#include <cassert>
#include <cstdio>
#include "APFloat.h"

using namespace llvm;

int main (void)
{
  APFloat d (APFloat::IEEEsingle, APFloat::fcZero, false);

  d.convertFromString("340282356779733661637539395458142568448.", APFloat::rmNearestTiesToEven);

  char s[400];
  d.convertToHexString(s, 0, false, APFloat::rmNearestTiesToEven);

  printf("%s\n", s);
  return 0;
}
