#include <cassert>
#include <cstdio>
#include "APFloat.h"

using namespace llvm;

int main (void)
{
  APFloat d (APFloat::IEEEsingle, APFloat::fcZero, false);

  for (int i = 0; i < 100000; i++)
    d.convertFromString("1.", APFloat::rmNearestTiesToEven);

  char s[400];
  d.convertToHexString(s, 0, false, APFloat::rmNearestTiesToEven);

  printf("%s\n", s);
  return 0;
}
